<?php

require_once 'PHPUnit/Framework.php';

require_once ROOT_PATH . '/symfony/lib/vendor/symfony/lib/helper/TagHelper.php';

/**
 * Test class for Button.
 * Generated by PHPUnit on 2011-04-22 at 00:50:30.
 */
class ButtonTest extends PHPUnit_Framework_TestCase {

    /**
     * @var Button
     */
    protected $button;

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp() {
        $this->button = new Button;
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     */
    protected function tearDown() {

    }

    public function test__toString_WithoutProperties() {
        $this->button->setIdentifier('Save');
        $expectedAttributes = array(
            'type="button"',
            'class="plainbtn"',
            'id="btnSave"',
            'name="btnSave"',
            'onmouseover="this.className=\'plainbtn plainbtnhov\'"',
            'onmouseout="this.className=\'plainbtn\'"',
            'value="Save"',
        );
        $html = $this->button->__toString();

        foreach ($expectedAttributes as $attribute) {
            $this->assertRegExp("/{$attribute}/", $html);
        }
    }

    public function test__toString_WithProperties() {
        $this->button->setIdentifier('Search_Button');
        $this->button->setProperties(array(
            'label' => 'Search',
            'id' => 'cmdSearch',
            'class' => 'longbtn',
            'type' => 'submit',
            'name' => '_search',
        ));
        $expectedAttributes = array(
            'type="submit"',
            'class="longbtn"',
            'id="cmdSearch"',
            'name="_search"',
            'onmouseover="this.className=\'longbtn longbtnhov\'"',
            'onmouseout="this.className=\'longbtn\'"',
            'value="Search"',
        );
        $html = $this->button->__toString();

        foreach ($expectedAttributes as $attribute) {
            $this->assertRegExp("/{$attribute}/", $html);
        }
    }

}

?>
